(462, 7, 'our_solutions_1_solution_title', 'PROCUREMENT'),
(463, 7, '_our_solutions_1_solution_title', 'field_60d579661a0b0'),
(464, 7, 'our_solutions_1_solution_description', 'Electronic procurement, also known as e-procurement, enables the electronic purchase of products and services via a central software program in which all suppliers are linked. A digitized ordering process speeds up procurement and reduces costs while increasing transparency, as orders can be placed with several suppliers in a single ordering process.\r\n'),
(465, 7, '_our_solutions_1_solution_description', 'field_60d579761a0b1'),
(466, 7, 'our_solutions_1_solution_features', '<ul>\r\n 	<li>place several orders with one click</li>\r\n 	<li>Integration of your suppliers and your special conditions</li>\r\n 	<li>store release processes</li>\r\n 	<li>Create personal order lists</li>\r\n</ul>'),
(467, 7, '_our_solutions_1_solution_features', 'field_60d579b71a0b2'),
(468, 7, 'our_solutions_1_solution_page_link', ''),
(469, 7, '_our_solutions_1_solution_page_link', 'field_60ffba5459670'),
(470, 7, 'our_solutions_2_solution_title', 'CONTRACT MANAGEMENT'),
(471, 7, '_our_solutions_2_solution_title', 'field_60d579661a0b0'),
(472, 7, 'our_solutions_2_solution_description', 'With the help of the software, the entrepreneur can quickly manage contracts, make decisions more efficiently and minimize administrative work. Documents are stored electronically in a central database and you always keep track of deadlines and obligations. The software reminds the entrepreneur in time of upcoming dates and deadlines.\r\n'),
(473, 7, '_our_solutions_2_solution_description', 'field_60d579761a0b1'),
(474, 7, 'our_solutions_2_solution_features', '<ul>\r\n 	<li>Manage contracts quickly and easily</li>\r\n 	<li>Make decisions for future contract situations more efficiently</li>\r\n 	<li>Dates and obligations always at a glance</li>\r\n 	<li>Transparent and secure filing</li>\r\n</ul>'),
(475, 7, '_our_solutions_2_solution_features', 'field_60d579b71a0b2'),
(476, 7, 'our_solutions_2_solution_page_link', ''),
(477, 7, '_our_solutions_2_solution_page_link', 'field_60ffba5459670'),
(478, 7, 'our_solutions_3_solution_title', 'INVOICE MANAGEMENT'),
(479, 7, '_our_solutions_3_solution_title', 'field_60d579661a0b0'),
(480, 7, 'our_solutions_3_solution_description', 'Accounts payable management is the basis for fast and efficient processing of incoming invoices. This eliminates many tedious and costly manual steps. Digital invoices simplify and accelerate the entire invoicing process from invoicing to approval. The entrepreneur eliminates mountains of paper and frees his employees from monotonous work.\r\n'),
(481, 7, '_our_solutions_3_solution_description', 'field_60d579761a0b1'),
(482, 7, 'our_solutions_3_solution_features', '<ul>\r\n 	<li>a clear dashboard</li>\r\n 	<li>automatic error detection</li>\r\n 	<li>single or multi-stage approval process</li>\r\n 	<li>digital archiving and legally secure storage of invoices</li>\r\n</ul>'),
(483, 7, '_our_solutions_3_solution_features', 'field_60d579b71a0b2'),
