(486, 7, 'our_solutions_4_solution_title', 'VENDOR MANAGED INVENTORY'),
(487, 7, '_our_solutions_4_solution_title', 'field_60d579661a0b0'),
(488, 7, 'our_solutions_4_solution_description', 'VMI stands for \"Vendor Managed Inventory\", also known as \"Supplier-managed Inventory (SMI)\". This is a logistics tool for improving performance in the supply chain, where the supplier has access to the customer\'s inventory and demand data. With VMI, the supplier takes responsibility for the inventory of its products at the customer\'s site.\r\n\r\n'),
(489, 7, '_our_solutions_4_solution_description', 'field_60d579761a0b1'),
(490, 7, 'our_solutions_4_solution_features', '<ul>\r\n 	<li>No more delivery bottlenecks</li>\r\n 	<li>automatic delivery without an order</li>\r\n 	<li>better supplier loyalty</li>\r\n 	<li>simple stock monitoring</li>\r\n</ul>'),
(491, 7, '_our_solutions_4_solution_features', 'field_60d579b71a0b2'),
(492, 7, 'our_solutions_4_solution_page_link', ''),
