(494, 7, 'our_solutions_5_solution_title', 'eBILL'),
(495, 7, '_our_solutions_5_solution_title', 'field_60d579661a0b0'),
(496, 7, 'our_solutions_5_solution_description', 'eBill is the digital bill for Switzerland and Liechtenstein. With eBill, customers no longer receive bills by mail or e-mail, but directly into e-banking - exactly where they are paid. With just a few clicks, bills can be checked and paid while always maintaining complete control. Thanks to eBill, customers pay more reliably, paper consumption is reduced and security is increased.\r\n'),
(497, 7, '_our_solutions_5_solution_description', 'field_60d579761a0b1'),
(498, 7, 'our_solutions_5_solution_features', '<ul>\r\n 	<li>Issue Invoice efficiently, securely and cost-effectively</li>\r\n 	<li>increase customer satisfaction\r\n</li>\r\n 	<li>Receive your money more punctually</li>\r\n 	</ul>'),
