(663, 101, '_our_solutions_0_solution_title', 'field_60d579661a0b0'),
(664, 101, 'our_solutions_0_solution_description', 'io-market\'s EDI interface enables companies to send and receive business data, such as purchase orders, in a structured, machine-readable format. This means that the incoming order can be transferred directly into the system and executed without further processing, and without the need to manually enter and file the order in advance. This solution works for all paper-based business transactions, such as invoices, delivery bills, order confirmations, etc.'),
(665, 101, '_our_solutions_0_solution_description', 'field_60d579761a0b1'),
(666, 101, 'our_solutions_0_solution_features', '<ul>\r\n 	<li>Invoices do not have to be typed</li>\r\n 	<li>Receive and place orders directly into your ERP system</li>\r\n 	<li>Order confirmations and delivery notes can be directly assigned to the order and filed.</li>\r\n</ul>'),
(667, 101, '_our_solutions_0_solution_features', 'field_60d579b71a0b2'),
