(1336, 111, '_our_solutions_1_solution_title', 'field_60d579661a0b0'),
(1337, 111, 'our_solutions_1_solution_description', 'Electronic procurement, also known as e-procurement, enables the electronic purchase of products and services via a central software program in which all suppliers are linked. A digitized ordering process speeds up procurement and reduces costs while increasing transparency, as orders can be placed with several suppliers in a single ordering process.\r\n'),
(1338, 111, '_our_solutions_1_solution_description', 'field_60d579761a0b1'),
(1339, 111, 'our_solutions_1_solution_features', '<ul>\r\n 	<li>place several orders with one click</li>\r\n 	<li>Integration of your suppliers and your special conditions</li>\r\n 	<li>store release processes</li>\r\n 	<li>Create personal order lists</li>\r\n</ul>'),
(1340, 111, '_our_solutions_1_solution_features', 'field_60d579b71a0b2'),
