(1344, 111, '_our_solutions_2_solution_title', 'field_60d579661a0b0'),
(1345, 111, 'our_solutions_2_solution_description', 'With the help of the software, the entrepreneur can quickly manage contracts, make decisions more efficiently and minimize administrative work. Documents are stored electronically in a central database and you always keep track of deadlines and obligations. The software reminds the entrepreneur in time of upcoming dates and deadlines.\r\n'),
(1346, 111, '_our_solutions_2_solution_description', 'field_60d579761a0b1'),
(1347, 111, 'our_solutions_2_solution_features', '<ul>\r\n 	<li>Manage contracts quickly and easily</li>\r\n 	<li>Make decisions for future contract situations more efficiently</li>\r\n 	<li>Dates and obligations always at a glance</li>\r\n 	<li>Transparent and secure filing</li>\r\n</ul>'),
(1348, 111, '_our_solutions_2_solution_features', 'field_60d579b71a0b2'),
