(1352, 111, '_our_solutions_3_solution_title', 'field_60d579661a0b0'),
(1353, 111, 'our_solutions_3_solution_description', 'Accounts payable management is the basis for fast and efficient processing of incoming invoices. This eliminates many tedious and costly manual steps. Digital invoices simplify and accelerate the entire invoicing process from invoicing to approval. The entrepreneur eliminates mountains of paper and frees his employees from monotonous work.\r\n'),
(1354, 111, '_our_solutions_3_solution_description', 'field_60d579761a0b1'),
(1355, 111, 'our_solutions_3_solution_features', '<ul>\r\n 	<li>a clear dashboard</li>\r\n 	<li>automatic error detection</li>\r\n 	<li>single or multi-stage approval process</li>\r\n 	<li>digital archiving and legally secure storage of invoices</li>\r\n</ul>'),
(1356, 111, '_our_solutions_3_solution_features', 'field_60d579b71a0b2'),
