(1359, 111, 'our_solutions_4_solution_title', 'VENDOR MANAGED INVENTORY'),
(1360, 111, '_our_solutions_4_solution_title', 'field_60d579661a0b0'),
(1361, 111, 'our_solutions_4_solution_description', 'VMI stands for \"Vendor Managed Inventory\", also known as \"Supplier-managed Inventory (SMI)\". This is a logistics tool for improving performance in the supply chain, where the supplier has access to the customer\'s inventory and demand data. With VMI, the supplier takes responsibility for the inventory of its products at the customer\'s site.\r\n\r\n'),
(1362, 111, '_our_solutions_4_solution_description', 'field_60d579761a0b1'),
(1363, 111, 'our_solutions_4_solution_features', '<ul>\r\n 	<li>No more delivery bottlenecks</li>\r\n 	<li>automatic delivery without an order</li>\r\n 	<li>better supplier loyalty</li>\r\n 	<li>simple stock monitoring</li>\r\n</ul>'),
